# app.R  — ShinyLive-ready
library(shiny)
library(bslib)

# ---- Colors ----
LIGHT_BLUE  <- "#767da2"  # inputs
LIGHT_GREY  <- "#a4aac1"  # first bar (replaces blue)
ORANGE_BAR  <- "#cd6118"  # keep your orange

dark_theme <- bs_theme(
  version = 5,
  bg = "#1d1f2b", fg = "#e1e2ea",
  primary = "#cd6118", secondary = "#5a5f80"
) |>
  bs_add_rules("
    :root{
      --bs-body-font-family: 'Lexend', system-ui, -apple-system, Segoe UI, Roboto, 'Helvetica Neue', Arial, sans-serif;
      --bs-font-monospace: 'Recursive', ui-monospace, SFMono-Regular, Menlo, Consolas, 'Liberation Mono', monospace;
    }
  ") |>
  bs_add_rules(sprintf("
    .container-fluid{ max-width:1100px; padding-top:calc(16px + env(safe-area-inset-top)); }
    .shiny-input-container{ margin-bottom:10px; }
    .no-stack{ display:flex; gap:16px; align-items:flex-start; }
    .no-stack .left{ flex:0 0 330px; min-width:260px; }
    .no-stack .right{ flex:1 1 auto; min-width:0; }
    .no-stack .left h4{ margin-top:14px; }
    #ifr-root{ padding-bottom:32px; }

    #tabBox{
      background: var(--bs-body-bg) !important;
      border: 1.5px solid %1$s !important;
      padding: 12px; border-radius: .6rem; box-shadow:none;
    }
    #stats{ font-size:16px; margin-top:10px; }
    #tabBox strong{ color: var(--bs-body-color); }

    .form-control, .form-select, .selectize-input{
      background-color:%1$s !important; color:var(--bs-body-color) !important;
      border:1px solid %1$s !important; box-shadow:none !important;
    }
    .form-control:focus, .form-select:focus, .selectize-input.focus{
      border-color:#cd6118 !important; outline:0 !important;
      box-shadow:0 0 0 .15rem rgba(205,97,24,.35) !important;
    }
    .selectize-dropdown, .selectize-dropdown .active{
      background:#44496f !important; color:var(--bs-body-color) !important; border-color:#5a5f80 !important;
    }

    table, th, td{
      color:var(--bs-body-color) !important; background-color:transparent !important;
      border-color:#adbdd1 !important; font-size:.95em;
    }
    thead, caption{ color:var(--bs-body-color) !important; }

    @media (max-width:576px){
      .no-stack{ flex-direction:column; gap:12px; }
      .no-stack .left, .no-stack .right{ flex:1 1 100%%; min-width:0; }
      .no-stack .left h4{ margin-top:10px; }
      .container-fluid{
        padding-top:calc(20px + env(safe-area-inset-top)); padding-left:12px; padding-right:12px;
      }
    }
  ", LIGHT_BLUE))

ui <- page_fluid(
  theme = dark_theme,
  tags$head(
    # --- Google Fonts (load in the browser; no R downloads) ---
    tags$link(rel = "preconnect", href = "https://fonts.googleapis.com"),
    tags$link(rel = "preconnect", href = "https://fonts.gstatic.com", crossorigin = "anonymous"),
    tags$link(
      href = "https://fonts.googleapis.com/css2?family=Lexend:wght@300;400;500&family=Recursive:wght,MONO@400,1&display=swap",
      rel  = "stylesheet"
    ),

    # iFrame Resizer (safe when embedded)
    tags$script(src = "https://cdn.jsdelivr.net/npm/iframe-resizer/js/iframeResizer.contentWindow.min.js"),
    tags$script(HTML(
      "document.addEventListener('shiny:idle', function () {
         if (window.parentIFrame && window.parentIFrame.size) { window.parentIFrame.size(); }
       });"
    ))
  ),

  tags$head(
    # keep your existing links/scripts...
    tags$style(HTML("#barPlot img{display:block;width:100%;height:auto!important;}")),

    # Send viewport height + a 'resizeTick' on every resize/orientation change
    tags$script(HTML("
    (function () {
      function send() {
        if (!window.Shiny) return;
        Shiny.setInputValue('vh_px', window.innerHeight, {priority:'event'});
        Shiny.setInputValue('resizeTick', Date.now(),     {priority:'event'});
      }
      window.addEventListener('load', send, {once:true});
      document.addEventListener('shiny:connected', send);
      window.addEventListener('resize', send);
      window.addEventListener('orientationchange', send);
    })();
  ")),

    # Imperatively set the pixel height of the plot container
    tags$script(HTML("
    Shiny.addCustomMessageHandler('setPlotHeight', function(px){
      var el = document.getElementById('barPlot');
      if (el) el.style.height = String(px) + 'px';
    });
  "))
  ),

  div(id = "ifr-root", `data-iframe-height` = "true",
      div(class = "no-stack",
          div(class = "left",
              h4("Cell counts (2×2)"),
              fluidRow(
                column(6, numericInput("a", "Row 1 • Col 1 (a)", 12, min = 0, step = 1)),
                column(6, numericInput("b", "Row 1 • Col 2 (b)",  8, min = 0, step = 1))
              ),
              fluidRow(
                column(6, numericInput("c", "Row 2 • Col 1 (c)",  5, min = 0, step = 1)),
                column(6, numericInput("d", "Row 2 • Col 2 (d)", 15, min = 0, step = 1))
              ),
              h4("Labels"),
              fluidRow(
                column(6, textInput("r1", "Row 1", "Group A")),
                column(6, textInput("r2", "Row 2", "Group B"))
              ),
              fluidRow(
                column(6, textInput("c1", "Col 1", "Yes")),
                column(6, textInput("c2", "Col 2", "No"))
              ),
              h4("Display"),
              selectInput("view", "Show values as:", c("Counts","Row %","Column %","Total %")),
              checkboxInput("show_exp", "Show expected counts table", FALSE),
              actionButton("reset", "Reset", class = "btn btn-secondary")
          ),
          div(class = "right",
              div(id = "tabBox",
                  strong(textOutput("tblTitle", inline = TRUE)),
                  tableOutput("xtab"),
                  uiOutput("expBox")
              ),
              br(),
              plotOutput("barPlot"),
              div(id = "stats", textOutput("summary"))
          )
      )
  )
)

server <- function(input, output, session) {

  plotHeight <- reactive({
    w <- session$clientData$output_barPlot_width
    if (is.null(w) || !is.finite(w) || w <= 0) w <- 700

    compact  <- (w < 520)
    h_from_w <- if (compact) max(240, 0.44 * w) else max(280, min(500, 0.52 * w))

    vh <- input$vh_px
    if (is.numeric(vh) && is.finite(vh) && vh > 0) {
      # cap by viewport height to prevent the moat
      cap <- if (compact) 0.46 * vh else 0.52 * vh
      h_from_w <- min(h_from_w, cap)
    }
    round(h_from_w)
  })

  # Whenever the height changes (or the window resizes), set the container's CSS height
  observeEvent(list(plotHeight(), input$resizeTick), {
    session$sendCustomMessage('setPlotHeight', plotHeight())
  }, ignoreInit = FALSE)


  xtab_counts <- reactive({
    mat <- matrix(
      c(input$a, input$b, input$c, input$d),
      nrow = 2, byrow = TRUE,
      dimnames = list(
        c(input$r1, input$r2),
        c(input$c1, input$c2)
      )
    )
    mat[is.na(mat) | mat < 0] <- 0
    round(mat, 0)
  })

  observeEvent(input$reset, {
    updateNumericInput(session, "a", value = 12)
    updateNumericInput(session, "b", value = 8)
    updateNumericInput(session, "c", value = 5)
    updateNumericInput(session, "d", value = 15)
    updateTextInput(session, "r1", value = "Group A")
    updateTextInput(session, "r2", value = "Group B")
    updateTextInput(session, "c1", value = "Yes")
    updateTextInput(session, "c2", value = "No")
    updateSelectInput(session, "view", selected = "Counts")
    updateCheckboxInput(session, "show_exp", value = FALSE)
  })

  output$tblTitle <- renderText({
    switch(input$view,
           "Counts"   = "Observed counts",
           "Row %"    = "Row percentages",
           "Column %" = "Column percentages",
           "Total %"  = "Total percentages"
    )
  })

  output$xtab <- renderTable({
    K <- xtab_counts()
    N <- sum(K)
    if (N == 0) return(matrix("", nrow = 2, ncol = 2))

    show <- switch(input$view,
                   "Counts"   = addmargins(K),
                   "Row %"    = { P <- 100 * sweep(K, 1, rowSums(K), "/"); P[!is.finite(P)] <- 0; addmargins(P) },
                   "Column %" = { P <- 100 * sweep(K, 2, colSums(K), "/"); P[!is.finite(P)] <- 0; addmargins(P) },
                   "Total %"  = addmargins(100 * K / N)
    )
    if (input$view == "Counts") round(show, 0) else apply(show, c(1, 2), function(x) sprintf('%.1f%%', x))
  }, rownames = TRUE, colnames = TRUE, bordered = TRUE, spacing = "s")

  output$expBox <- renderUI({
    if (!isTRUE(input$show_exp)) return(NULL)
    tagList(br(), strong("Expected counts (under independence)"), tableOutput("xtabExp"))
  })

  output$xtabExp <- renderTable({
    K <- xtab_counts()
    N <- sum(K)
    if (N == 0) return(matrix("", nrow = 2, ncol = 2))
    E <- outer(rowSums(K), colSums(K)) / N
    round(addmargins(E), 2)
  }, rownames = TRUE, colnames = TRUE, bordered = TRUE, spacing = "s")

  # --- Bars (explicit colors so it looks right without 'thematic') ---
  output$barPlot <- renderPlot({
    K <- xtab_counts(); N <- sum(K)
    if (N == 0) return()

    # width-driven compact mode
    w <- session$clientData$output_barPlot_width
    if (is.null(w) || !is.finite(w) || w <= 0) w <- 700
    compact <- (w < 520)

    # exact fraction of the device reserved for the legend row
    legend_frac <- if (compact) 0.16 else 0.13   # smaller = closer to the plot

    ## ---------------------------
    ## Panel 1: main plot (top)
    ## ---------------------------
    par(fig = c(0, 1, legend_frac, 1), new = FALSE)
    par(
      mar = if (compact) c(2.8, 4.6, 1.0, 1.2) else c(3.2, 5.0, 1.2, 1.2),
      col.lab = "#e1e2ea", col.axis = "#dfe1ec", fg = "#adbdd1",
      xpd = NA
    )

    P  <- prop.table(K, 1)
    bp <- barplot(
      t(P),
      beside = FALSE,
      col    = c("#8a1e00", ORANGE_BAR),
      ylim   = c(0, 1),
      border = NA,
      ylab   = "Row proportion",
      xaxt   = "n", yaxt = "s"
    )
    axis(1, at = bp, labels = rownames(K), tick = FALSE)

    ## ---------------------------
    ## Panel 2: legend (bottom)
    ## ---------------------------
    par(fig = c(0, 1, 0, legend_frac), new = TRUE)
    par(mar = c(0, 4, 0, 1) + 0.1)   # no top/bottom gap around legend row
    plot.new()
    legend(
      "center",
      legend   = colnames(K),
      fill     = c("#8a1e00", ORANGE_BAR),
      horiz    = TRUE, bty = "n",
      text.col = "#e1e2ea",
      cex      = if (compact) 0.90 else 0.95,
      x.intersp = 1.0, y.intersp = 0.9
    )
  },
  height = function() plotHeight(),
  res = 96, bg = "transparent", execOnResize = TRUE)


  output$summary <- renderText({
    K <- xtab_counts()
    N <- sum(K)
    if (N == 0) return('No data yet.')
    suppressWarnings({ ct <- chisq.test(K, correct = FALSE) })
    chi2 <- unname(ct$statistic); df <- unname(ct$parameter); p <- ct$p.value
    phi  <- if (N > 0) sqrt(chi2 / N) else NA_real_
    if (any(ct$expected < 5)) {
      fp <- tryCatch(fisher.test(K)$p.value, error = function(e) NA_real_)
      sprintf('N = %d  |  χ²(%d) = %.3f  p = %.4f  |  φ = %.3f  |  Fisher p = %.4f (expected < 5)',
              N, df, chi2, p, phi, fp)
    } else {
      sprintf('N = %d  |  χ²(%d) = %.3f  p = %.4f  |  φ = %.3f', N, df, chi2, p, phi)
    }
  })
}

shinyApp(ui, server)
